mapper.insert:
insert
  into
       board 
       (
		 BOARD_NO
		,BOARD_TYPE
		,BOARD_TITLE
		,BOARD_CONTENT
		,CREATE_DATE
		,MODIFY_DATE
		,THUMBNAIL
		,RECRUIT_YN
		,COUNT
		,STATUS
		,MEM_NO
       )
values
       (
       seq_bno.nextval
      ,#{boardType}
      ,#{boardTitle}
      ,#{boardContent}
      ,sysdate
      ,#{modifyDate}
      ,#{thumbnail}
      ,#{recruitYN}
      ,0
      ,'Y'
      ,#{memNo}
       )
