mapper.update:
UPDATE
	   board
   SET
       board_title=#{boardTitle},
       board_content=#{boardContent},
       modify_date=#{modifyDate}
 WHERE
       board_no=#{boardNo}
   AND
       board_type=#{boardType}

   UPDATE
		  attachment
      SET
		  origin_name = #{originName},
		  change_name = #{changeName}
    WHERE
		  board_no=#{boardNo}
	  AND
	      board_type=#{boardType}
