mapper.select:
select diary_no, TO_CHAR(create_date, 'YYYY-MM-DD') "CREATE_DATE", diary_category, diary_content, diary.status, diary.mem_no, diary_thumbnail, member.nickname from diary join member
on (member.mem_no=diary.mem_no)
where create_date >= #{startDate}
and create_date-1 <= #{endDate}
and diary.mem_no = ${memNo} and diary.status = 'Y'
and diary_category = #{diaryCategory}

mapper.insert:
insert into diary values ( seq_dno.nextval, sysdate, #{diaryCategory}, #{diaryContent}, 'Y', #{memNo}
, #{diaryThumbnail} )
, null )

mapper.update:
update
		diary
set
		create_date = #{createDate}, 
		diary_category = #{diaryCategory},
		diary_content = #{diaryContent}



		, diary_thumbnail = #{diaryThumbnail}

where
		diary_no  = #{diaryNo}
