mapper.update:
UPDATE MEMBER
SET NAME = #{name}, EMAIL = #{email}, NICKNAME = #{nickName}, PHONE = #{phone}, MODIFY_DATE = SYSDATE, FARMER_CODE = #{farmerCode}, CAREER = #{career}
WHERE MEM_ID = #{memId}
AND MEM_STATUS = 'Y'

UPDATE MEMBER
SET MEM_STATUS = 'N'
WHERE MEM_ID = #{memId}

UPDATE MEMBER
SET MEM_STATUS = 'N'
WHERE MEM_ID = #{memId}

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MEM_ID = #{memId}
